<script>import TodoComponent from "./TodoComponent.svelte";
export let slices = [];
export let components = {};
export let context = {};
export let defaultComponent = void 0;
</script>

<!--
  @component
  Component to render an array of Prismic slices.

  @example Rendering a Slice Zone:
	```svelte
		<SliceZone
			slices={document.data.slices}
			components={{
				block_quote: BlockQuote,
				hero_image: HeroImage
			}}
		/>
	```
-->

{#each slices as slice, index}
	{@const type = "slice_type" in slice ? slice.slice_type : slice.type}
	{@const Component = components[type] || defaultComponent}
	{#if Component}
		{#if slice.__mapped}
			{@const { __mapped, ...mappedProps } = slice}
			<svelte:component this={Component} {...mappedProps} />
		{:else}
			<svelte:component this={Component} {slice} {slices} {context} {index} />
		{/if}
	{:else}
		<TodoComponent {slice} />
	{/if}
{/each}
